def update_list_to_longs (update_list, number_of_updates, number_of_leds):
    """
    Creates 4-byte integers with each bit being 1 if the corresponding LED is on and 0 if off
    :param update_list: a list of lists which have: update_number, led_number, 1/0 (which means on/off)
    for example
    [[0, 1, 1], # in update 0, the first led (the one closest to the center) will be on.
    [0, 2, 0],  #              the second led will be off, etc.
    :param number_of_updates:
    :param number_of_leds:
    :return: list of 4-byte integers storing LED states, one for each update.
    """

    led_longs = []
    for update in range(number_of_updates):
        led_long = 0

        for led in range(number_of_leds):
            led_long = led_long + (update_list[update*number_of_leds + led][2] << led)

        led_longs.append(led_long)
    return led_longs

def sort_update_list(update_list):
    return sorted(update_list, key=itemgetter(0,1))  # Sort the list first on the zeroth element of the sublists, and then by first element

def led_longs_to_arduino(led_longs):
    """
    Creates a matrix (list of lists) that the Arduino sketch will use to turn on and off LEDs.
    The output will be formatted so you can copy and paste the matrix definition into the Arduino sketch.
    :param led_longs: one long integer for each update
    :return: outputs a file formatted as an Arduino array 4 bytes to an update. least significant byte to most.
    """
    f = open('rotaviz_images.h', "w")

    f.write('// Rotaviz image. Each row is an update with 4 bytes least significant byte first. MSB order. \n')
    f.write('img[{}][4] = {{\n'.format(len(led_longs)))  # The name of the array and the opening curly brackets.
    for led_long in led_longs:
        f.write('\t{')
        b = bytearray(led_long.to_bytes(4, byteorder='big', signed=False))  # Splits the integer up into 4 bytes in an mutable object
        for led_byte in reversed(b):
            f.write('{}, '.format(led_byte))
        f.write('},\n')
    f.write('};\n') # End of this image
    f.close()
    return

def reverse_bits(n):
        result = 0
        for i in range(32):
            result <<= 1
            result |= n & 1
            n >>= 1
        return result


def led_longs_to_fast_arduino(led_longs):
    """
    Creates a matrix for the Arduino with one integer per update,
    If pins D6&D7 are set to input and they are wired to D10 and D11, one BYTE can be directly written to PORTD
        Latch D4
        Clock D5
        Least D6
              D7
              D8
        Most  D9
        Least D10
        2nd   D11
    :param led_longs:
    :return:
    """
    f = open('rotaviz_images_fast_fixed.h', "w")

    f.write('// Rotaviz image. Each row is an update with 8 bytes to write directly to PORTB. \n')
    f.write('byte img[{}][8] = {{\n'.format(len(led_longs))) # The name of the array and the opening curly brackets.
    led_longs_reversed = [reverse_bits(x) for x in led_longs]
    for led_long in led_longs_reversed:
        f.write('\t{')
        b = bytearray(led_long.to_bytes(4, byteorder='big', signed=False))  # Splits the integer up into 4 bytes in an mutable object
        # Each byte corresponds to a shift register on a certain Arduino pin D8 - D11
        d8 =[b[1] >> i & 1 for i in range(7,-1,-1)]
        d9 = [b[0] >> i & 1 for i in range(7,-1,-1)]
        d10 = [b[3] >> i & 1 for i in range(7,-1,-1)]
        d11 = [b[2] >> i & 1 for i in range(7,-1,-1)]
        for i in range(8):
            #f.write('{}, '.format(int(d11[7-i]*8 + d10[7-i]*4 + d9[7-i]*2 + d8[7-i]*1)))
            f.write('{}, '.format(int(d11[7-i]*1 + d10[7-i]*2 + d9[7-i]*4 + d8[7-i]*8)))
        f.write('},\n')
    f.write('};\n') # End of this image
    f.close()
    return

def visualize_image(led_longs, number_of_leds, number_of_updates):
    """
    Simulates the display of the rotaviz.
    :param led_longs:
    :param number_of_leds:
    :param number_of_updates:
    :return:
    """
    pixels_per_led = 4
    img_size = (number_of_leds * 2 + 1) * pixels_per_led
    win = GraphWin("rotaviz", img_size , img_size)
    degrees_per_update = 360 // number_of_updates
    update_number = 0
    for led_long in led_longs:
        #print(led_long, end=' ')
        for degree in range(degrees_per_update):
            for i in range(number_of_leds):
                #print(led_long & 1<<i, end=" ")
                if (led_long & 1<<i):
                    rad = - radians(degree + degrees_per_update * update_number)
                    x = cos(rad) * (i + 1) * pixels_per_led
                    y = sin(rad) * (i + 1) * pixels_per_led
                    Point(x + img_size // 2, img_size // 2 - y ).draw(win)
            #print(' ')
        update_number += 1
    win.getMouse()
    win.close()
    return


